import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Sidebar from "./Sidebar";

const Dropdown = ( items ) => {
    const [submenu, setSubmenu] = useState(false);
    const showsubmenu = () => setSubmenu(!submenu)
    return (
        <>
            <SidebarLink to={items.path} onClick={items.submenu && showsubmenu}>
                <div>
                    {items.icon}
                    <SidebarLabel>{items.name}</SidebarLabel>
                </div>
                <div>
                    {
                        items.submenu && submenu ? items.iconOpended : items.submenu ? items.iconClosed :null
                    }
                </div>
            </SidebarLink>
            {submenu && items.submenu.map((items, index)=>{
                return (
                    <dropDownLink to={items.path} key={index}>
                        {items.icon}
                        <SidebarLabel>{items.title}</SidebarLabel>
                    </dropDownLink>
                )
            })}
        </>
    )

}

export default Dropdown;
