import React, { Children } from 'react';
import { FaBars, FaHome, FaFillDelete } from 'react-icons/fa'
import { NavLink } from 'react-router-dom';

const Sidebar = ({ Children }) => {
    const sideBaritems = [
        {
            path: "/Home",
            name: "home",
            icon:<FaHome/>
        },
        {
            path: "/Project_Status",
            name: "ProjectStatus",

        },
        {
            path: "/Update_Employee",
            name: "updateEmployee",

        },
        {
            path: "/Remove_Employee",
            name: "RemoveEmployee",
            icon:<FaFillDelete/>
        },
    ]

    return (
        <div className='container'>
            <div className='sidebar'>
                <div className='top'>
                    <div className='bars'>
                        <FaBars />
                        <h1 className='Kaavian'>KAVN</h1>
                    </div>
                </div>
                {
                    sideBaritems.map((item, index) => {
                      return  <NavLink to={item.path} key={index} className='link' activeclassName='active'>
                            <div className='icon'>{item.icon}</div>
                            <div className='link_text'>{item.name}</div>
                        </NavLink>
                    })
                }
            </div>
            <main>{ Children }</main>
        </div>
    )
}

export default Sidebar;
