import React from 'react';
import { useState } from 'react';
import { FaBars, FaHome, FaEraser } from 'react-icons/fa'
import { GrStatusInfo,GrDown,GrInProgress } from "react-icons/gr";
import { MdUpdate,MdOutlineThumbUp } from "react-icons/md";
import { NavLink } from 'react-router-dom';

const Sidebar = ({ Children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen (!isOpen);
    const sideBaritems = [
        {
            path: "/",
            name: "Home",
            icon:<FaHome/>
        },
        {
            path: "/ProjectStatus",
            name: "ProjectStatus",
            icon:<GrStatusInfo/>,
            iconClosed:<GrDown/>,
            iconOpened:<Grup/>, 

            dropDown: [ 
                {
                    path: "/ProjectStatus/Completed",
                    name:"Completed",
                    icon:<MdOutlineThumbUp/>
                    
                },
                {
                    path: "/ProjectStatus/OnProcess",
                    name: "OnProcess",
                    icon: <GrInProgress/>
                },
            ]
        },
        {
            path: "/UpdateEmployee",
            name: "UpdateEmployee",
            icon:<MdUpdate/>

        },
        {
            path: "/RemoveEmployee",
            name: "RemoveEmployee",
            icon:<FaEraser/>
        }
    ]

    return (
        <div className='container'>
            <div style={{width: isOpen ? '300px' : '50px'}}className='sidebar'>
                <div className='top'>
                    <h1 style={{display: isOpen ? 'block' : 'none'}} className='Admin'>ADMIN</h1>
                    <div style={{marginLeft: isOpen ? '50px' : '0px'}} className='bars'>
                        <FaBars onClick={toggle}/></div>
                </div>
                {/* <div className='adminname'>
                    <h2>Welcome ADMIN</h2>
                    <div><GrUserAdmin/></div>
                </div> */}
                {
                    sideBaritems.map((item, index) => (
                      <NavLink to={item.path} key={index} className='link' activeclassName='active'>
                            <div className='icon'>{item.icon}</div>
                            <div style={{display: isOpen ? 'block' : 'none'}} className='link_text'>{item.name}
                            </div>
                        </NavLink>
                    ))
                }
                
            </div>
            <main>{ Children }</main>
        </div>
        
    );
};

export default Sidebar;
