import React, { Children } from 'react';
import { useState } from 'react';
import { FaBars, FaHome, FaFillDelete, FaEraser } from 'react-icons/fa'
import { GrStatusInfo } from "react-icons/gr";
import { MdUpdate } from "react-icons/md";
import { NavLink } from 'react-router-dom';

const Sidebar = ({ Children }) => {
    const [isOpen, setIsOpen] = useState(false)
    const toggle = () => setIsOpen (!isOpen);
    const sideBaritems = [
        {
            path: "/Home",
            name: "home",
            icon:<FaHome/>
        },
        {
            path: "/Project_Status",
            name: "ProjectStatus",
            icon:<GrStatusInfo/>

        },
        {
            path: "/Update_Employee",
            name: "updateEmployee",
            icon:<MdUpdate/>

        },
        {
            path: "/Remove_Employee",
            name: "RemoveEmployee",
            icon:<FaEraser/>
        },
    ]

    return (
        <div className='container'>
            <div style={{width: isOpen ? '100px' : '50px'}}className='sidebar'>
                <div className='top'>
                <h1 className='Admin'>ADMIN</h1>
                    <div className='bars'>
                        <FaBars onClick={toggle}/>
                    </div>
                </div>
                {
                    sideBaritems.map((item, index) => {
                      return  <NavLink to={item.path} key={index} className='link' activeclassName='active'>
                            <div className='icon'>{item.icon}</div>
                            <div className='link_text'>{item.name}</div>
                        </NavLink>
                    })
                }
            </div>
            <main>{ Children }</main>
        </div>
    )
}

export default Sidebar;
